/* eslint-disable no-console */
/* eslint-disable react/prefer-stateless-function */
import React from 'react';
import PropTypes from 'prop-types';

import './App.css';

import Pokedex from './componets/Pokedex';
import pokemons from './data';
import Header from './componets/Header';
import Buttons from './componets/Buttons';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      btnTypeFilterValue: '',
    };
  }

  handelClick = (event) => {
    this.setState({ btnTypeFilterValue: event.target.key });
  }

  render() {
    const { btnTypeFilterValue } = this.props;

    return (
      <>
        <header>
          <Header />
        </header>
        <main>
          <div className="pokedex-container">
            <section className="filter-bar">
              <p>Filtros: </p>
              {pokemons.map((pokemon) => pokemon.type)
                .reduce((acc, cur) => (acc.includes(cur) ? acc : [...acc, cur]), [])
                .map((type) => <Buttons pokemonType={type} key={type} />) }
            </section>
            <Pokedex pokemons={pokemons} filter={btnTypeFilterValue} />
          </div>
        </main>
        <footer><p>MÃ³dulo 2 - Bloco 11 - Dia 2</p></footer>
      </>
    );
  }
}

export default App;

App.propTypes = { btnTypeFilterValu: PropTypes.string.isRequired };
