const INITIAL_STATE = [];

const REGISTER = 'REGISTER';

const registerReducer = (state = INITIAL_STATE,
  action) => {
  switch (action.type) {
    case REGISTER:
      return [
        ...state,
        ({
          id: state.payload.id,
          name: state.payload.name,
          age: state.payload.age,
          email: state.payload.email,
        }),
      ];
    default:
      return state;
  }
};

export { REGISTER, registerReducer };
