const INITIAL_STATE = {};

const LOGIN = 'LOGIN';

const loginReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case LOGIN:
      return action.payload.user;
    default:
      return state;
  }
};

export { loginReducer, LOGIN };
