const INITIAL_STATE = {
  username: '',
  password: '',
};

const LOGIN = 'LOGIN';

const loginReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case LOGIN:
      return { ...state, username: state.payload.username, password: state.payload.password };
    default:
      return state;
  }
};

export { loginReducer, LOGIN };
