/* eslint-disable react/prop-types */
import { Component } from 'react';
import { connect } from 'react-redux';

class Register extends Component {
  constructor() {
    super();

    this.state = {
      id: 0,
      name: '',
      age: 0,
      email: '',
    };
  }

  getInfos(event) {
    const { clients } = this.props;
    if (event.target.name === 'id') {
      this.setState({
        id: clients.length + 1,
      });
    } else {
      this.setState({
        [event.target.name]: event.target.value,
      });
    }
  }

  render() {
    const {
      id, name, age, email,
    } = this.state;

    const { clients } = this.props;

    return (
      <section>
        <form>
          <input type="text" name="id" value={id} placeholder={`${clients.length + 1}`} disabled />
          <label htmlFor="name">
            <input type="text" name="name" maxLength="40" value={name} onChange={this.getInfos} />
          </label>
          <label htmlFor="age">
            <input type="number" name="age" maxLength="3" value={age} onChange={this.getInfos} />
          </label>
          <label htmlFor="email">
            <input type="email" name="email" maxLength="40" value={email} onChange={this.getInfos} />
          </label>
        </form>
        <div>
          <button type="button">Voltar</button>
          <button type="button">Cadastrar</button>
        </div>
      </section>
    );
  }
}

const mapStateToProps = (state) => ({
  clients: state.registerReducer,
});

export default connect(mapStateToProps)(Register);
