import { LOGIN } from '../reducers/loginReducer';
import { REGISTER } from '../reducers/registerReducer';

const login = (user) => ({
  type: LOGIN,
  payload: {
    user,
  },
});

const register = ({
  id, name, age, email,
}) => ({
  type: REGISTER,
  payload: {
    id, name, age, email,
  },
});

export { login, register };
