/* eslint-disable react/prop-types */
import { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import '../bulma.min.css';
import '../login.css';

import { login as loginAction } from '../Redux/actions';

class LoginForm extends Component {
  constructor() {
    super();

    this.state = {
      username: '',
      password: '',
    };

    this.getTextInput = this.getTextInput.bind(this);
  }

  getTextInput(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  render() {
    const { username, password } = this.state;
    const { login } = this.props;
    return (
      <section className="form-login">
        <form>
          <div className="field">
            <label className="label mod-label" htmlFor="username">
              Usu√°rio
              <div className="control has-icons-left has-icons-right">
                <input className="input is-link" type="text" placeholder="Username" name="username" value={username} onChange={this.getTextInput} />
              </div>
            </label>
          </div>
          <div className="field">
            <label className="label mod-label" htmlFor="password">
              Senha
              <p className="control has-icons-left">
                <input className="input is-link" type="password" placeholder="Password" name="password" value={password} onChange={this.getTextInput} />
              </p>
            </label>
          </div>
          <div className="field">
            <p className="control has-text-centered">
              <Link to="/clients" className="button is-primary is-outlined is-normal is-rounded" onClick={() => login({ username, password })}>
                Login
              </Link>
            </p>
          </div>
        </form>
      </section>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  login: (user) => dispatch(loginAction(user)),
});

export default connect(null, mapDispatchToProps)(LoginForm);
