/* eslint-disable no-console */
/* eslint-disable react/prop-types */
import { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import '../login.css';

class Clients extends Component {
  render() {
    // const { clientList } = this.props;
    // const {
    //   id, name, age, email,
    // } = clientList;
    const { login, clients } = this.props;
    console.log(login);
    console.log(clients);

    if (!login.user) return <p className="title is-4 has-text-centered">Login n√£o efetuado</p>;
    if (clients.length < 1) {
      return (
        <section className="has-text-centered form-login">
          <p className="title is-4 has-text-centered">Nenhum cliente cadastrado</p>
          <Link to="/register" className="button is-primary is-outlined is-normal is-rounded">Cadastrar</Link>
        </section>
      );
    }
    return (
      <>
        <section>
          <p>Lista de Clientes:</p>
          <ul>
            <li>ok</li>
            {clients.map((client) => (
              <li key={client.id}>
                <ul>
                  <li>{`ID: ${client.id}`}</li>
                  <li>{`Nome: ${client.name}`}</li>
                  <li>{`Idade: ${client.age}`}</li>
                  <li>{`E-mail: ${client.email}`}</li>
                </ul>
              </li>
            ))}
          </ul>
          <Link to="/register" className="button is-primary is-outlined is-normal is-rounded">Cadastrar</Link>
        </section>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  login: state.loginReducer,
  clients: state.registerReducer,
});

export default connect(mapStateToProps)(Clients);
