import { Component } from 'react';

import '../bulma.min.css';
import '../login.css';

import { login as loginAction } from '../Redux/actions';

class LoginForm extends Component {
  render() {
    return (
      <section className="form-login">
        <form>
          <div className="field">
            <label className="label mod-label" htmlFor="username">
              Usu√°rio
              <div className="control has-icons-left has-icons-right">
                <input className="input is-link" type="text" placeholder="Text input" name="username" />
              </div>
            </label>
          </div>
          <div className="field">
            <label className="label mod-label" htmlFor="password">
              Senha
              <p className="control has-icons-left">
                <input className="input is-link" type="password" placeholder="Password" name="password" />
              </p>
            </label>
          </div>
          <div className="field">
            <p className="control has-text-centered">
              <button type="button" className="button is-primary is-outlined is-normal is-rounded">
                Login
              </button>
            </p>
          </div>
        </form>
      </section>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  login: (username, password) => dispatch(loginAction(username, password)),
});

export default LoginForm;
