/* eslint-disable react/prop-types */
import { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { register as registerAction } from '../Redux/actions';

class Register extends Component {
  constructor() {
    super();

    this.state = {
      id: 0,
      name: '',
      age: 0,
      email: '',
    };

    this.getInfos = this.getInfos.bind(this);
  }

  componentDidMount() {
    const { clients } = this.props;
    this.setState({
      id: (clients.length + 1),
    });
  }

  getInfos(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  render() {
    const {
      id, name, age, email,
    } = this.state;

    const { clients, register } = this.props;

    return (
      <section>
        <form>
          <p name="id" value={id}>{`ID: ${(clients.length + 1)}`}</p>
          <label htmlFor="name">
            Nome:
            <input type="text" name="name" maxLength="40" value={name} onChange={this.getInfos} />
          </label>
          <label htmlFor="age">
            Idade:
            <input type="number" name="age" maxLength="3" value={age} onChange={this.getInfos} />
          </label>
          E-mail:
          <label htmlFor="email">
            <input type="email" name="email" maxLength="40" value={email} onChange={this.getInfos} />
          </label>
        </form>
        <div>
          <Link to="/clients">Voltar</Link>
          <button
            type="button"
            onClick={() => register({
              id, name, age, email,
            })}
          >
            Cadastrar
          </button>
        </div>
      </section>
    );
  }
}

const mapStateToProps = (state) => ({
  clients: state.registerReducer,
});

const mapDispatchToProps = (dispatch) => ({
  register: (infos) => dispatch(registerAction(infos)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Register);
