const INITIAL_STATE = {
  cartItems: [],
};

const ADD_ITEM_TO_CART = 'ADD_ITEM_TO_CART';

const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ADD_ITEM_TO_CART:
      return { ...state, cartItems: state.cartItems.concat(element) };
    default:
      return state;
  }
};

export default reducer;
