/* eslint-disable react/prefer-stateless-function */
import React from 'react';

class Form extends React.Component {
  constructor() {
    super();

    this.state = {
      firstName: '',
      lastName: '',
      age: 0,
      attendance: false,
    };
  }

  getInfos(event) {
    const value = event.target.type === 'checkbox' ? event.target.checked : event.target.value;

    this.setState({
      [event.target.name]: value,
    });
  }

  render() {
    return (
      <section>
        <form>
          <label htmlFor="inputFirstName">
            Nome:
            <input type="text" name="inputFirstName" onChange={this.getInfos} max="20" />
          </label>
          <label htmlFor="inputLastName">
            Nome:
            <input type="text" name="inputLastName" onChange={this.getInfos} max="30" />
          </label>
          <label htmlFor="inputAge">
            Nome:
            <input type="number" name="inputAge" onChange={this.getInfos} max="3" />
          </label>
          <label htmlFor="inputAttendance">
            Vai comparecer?
            <input type="checkbox" name="inputAttendance" onChange={this.getInfos} />
          </label>
        </form>
        <button type="button" onClick>Cadastrar</button>
        <button type="button" onClick>Limpar</button>
      </section>
    );
  }
}

export default Form;
