import React from 'react';
import { connect } from 'react-redux';

class List extends React.Component {
  render() {
    const { envitedList } = this.props;
    return (
      <section>
        <h1>Lista de Convidados</h1>
        <ul>
          {envitedList.map((envited, index) => (
            <li key={index}>
              <ul>
                <li>{`Name: ${envited.firstName} ${envited.lastName}`}</li>
                <li>{`Idade: ${envited.age}`}</li>
                {envited.attendence === true ? <li>Confirmado</li> : <li>NÃ£o confirmado</li>}
              </ul>
            </li>
          ))}
        </ul>
      </section>
    );
  }
}

const mapStateToProps = (state) => ({
  envitedList: state.participant,
});

export default connect(mapStateToProps)(List);
