const INITIAL_STATE = {
  participant: {},
};

const ADD_PARTICIPANT = 'ADD_PARTICIPANT';

const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ADD_PARTICIPANT:
      return { ...state, participant: { name: state.name, age: state.age, checkBox: state.checkBox } };
    default:
      return state;
  }
};

export default reducer;
