const INITIAL_STATE = [];

const ADD_PARTICIPANT = 'ADD_PARTICIPANT';

const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ADD_PARTICIPANT:
      return [...state, (action.payload.participant)];
    default:
      return state;
  }
};

export default reducer;
