/* eslint-disable react/prefer-stateless-function */
import React from 'react';

class Form extends React.Component {
  constructor() {
    super();

    this.state = {
      participant:
      {
        firstName: '',
        lastName: '',
        age: 0,
        attendance: false,
      },
    };

    this.getInfos = this.getInfos.bind(this);
  }

  getInfos(event) {
    const value = event.target.type === 'checkbox' ? event.target.checked : event.target.value;

    this.setState({
      [event.target.name]: value,
    });
  }

  render() {
    const { participant } = this.state;
    const {
      firstName, lastName, age, attendance,
    } = participant;
    return (
      <section>
        <form>
          <label htmlFor="firstName">
            Nome:
            <input type="text" name="firstName" value={firstName} onChange={this.getInfos} max="20" />
          </label>
          <label htmlFor="lastName">
            Sobrenome:
            <input type="text" name="lastName" value={lastName} onChange={this.getInfos} max="30" />
          </label>
          <label htmlFor="age">
            Idade:
            <input type="number" name="age" value={age} onChange={this.getInfos} max="3" />
          </label>
          <label htmlFor="attendance">
            Vai comparecer?
            <input type="checkbox" name="attendance" checked={attendance} onChange={this.getInfos} />
          </label>
        </form>
        {/* <button type="button" onClick>Cadastrar</button> */}
        {/* <button type="button" onClick>Limpar</button> */}
      </section>
    );
  }
}

export default Form;
