const INITIAL_STATE = {
  participant: {},
};

const ADD_PARTICIPANT = 'ADD_PARTICIPANT';

const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ADD_PARTICIPANT:
      return {
        ...state, participant,
      };
    default:
      return state;
  }
};

export default reducer;
